const optionalDependencies = {"@tailcallhq/core-linux-x64-gnu":"v0.124.0","@tailcallhq/core-linux-x64-musl":"v0.124.0","@tailcallhq/core-linux-arm64-gnu":"v0.124.0","@tailcallhq/core-linux-arm64-musl":"v0.124.0","@tailcallhq/core-linux-ia32-gnu":"v0.124.0","@tailcallhq/core-darwin-arm64":"v0.124.0","@tailcallhq/core-darwin-x64":"v0.124.0","@tailcallhq/core-win32-x64-msvc":"v0.124.0","@tailcallhq/core-win32-arm64-msvc":"v0.124.0","@tailcallhq/core-win32-ia32-msvc":"v0.124.0"};
