const optionalDependencies = {"@tailcallhq/core-linux-x64-gnu":"v1.6.14","@tailcallhq/core-linux-x64-musl":"v1.6.14","@tailcallhq/core-linux-arm64-gnu":"v1.6.14","@tailcallhq/core-linux-arm64-musl":"v1.6.14","@tailcallhq/core-linux-ia32-gnu":"v1.6.14","@tailcallhq/core-darwin-arm64":"v1.6.14","@tailcallhq/core-darwin-x64":"v1.6.14","@tailcallhq/core-win32-x64-msvc":"v1.6.14","@tailcallhq/core-win32-arm64-msvc":"v1.6.14","@tailcallhq/core-win32-ia32-msvc":"v1.6.14"};
const os = process.platform
const arch = process.arch

const dependency = Object.keys(optionalDependencies).find((name) => name.includes(`${os}-${arch}`))
if (!dependency) {
  const redColor = "\x1b[31m"
  const resetColor = "\x1b[0m"
  console.error(`${redColor} Tailcall does not support platform ${os} arch ${arch} ${resetColor}`)
  process.exit(1)
}
